import { useState, useEffect } from "react";
import { Container, Row, Col } from "react-bootstrap";
import healty1 from "../assets/img/healty/healty1.jpg";
import healty2 from "../assets/img/healty/healty2.jpg";
import healty3 from "../assets/img/healty/healty3.jpg";
import healty4 from "../assets/img/healty/healty4.jpg";
import healty5 from "../assets/img/healty/healty5.jpg";



import { ArrowRightCircle } from 'react-bootstrap-icons';
import 'animate.css';
import TrackVisibility from 'react-on-screen';

export const Content = () => {
  const [loopNum, setLoopNum] = useState(0);
  const [isDeleting, setIsDeleting] = useState(false);
  const [text, setText] = useState('');
  const [delta, setDelta] = useState(300 - Math.random() * 100);
  const [index, setIndex] = useState(1);
  const toRotate = ["Web Developer", "Web Designer", "UI/UX Designer"];
  const period = 2000;

  useEffect(() => {
    let ticker = setInterval(() => {
      tick();
    }, delta);

    return () => { clearInterval(ticker) };
  }, [text])

  const tick = () => {
    let i = loopNum % toRotate.length;
    let fullText = toRotate[i];
    let updatedText = isDeleting ? fullText.substring(0, text.length - 1) : fullText.substring(0, text.length + 1);

    setText(updatedText);

    if (isDeleting) {
      setDelta(prevDelta => prevDelta / 2);
    }

    if (!isDeleting && updatedText === fullText) {
      setIsDeleting(true);
      setIndex(prevIndex => prevIndex - 1);
      setDelta(period);
    } else if (isDeleting && updatedText === '') {
      setIsDeleting(false);
      setLoopNum(loopNum + 1);
      setIndex(1);
      setDelta(500);
    } else {
      setIndex(prevIndex => prevIndex + 1);
    }
  }

  return (
    <section className="content" id="home">
      <Container>
        <Row className="aligh-items-center">
          <Col xs={12} md={4} xl={5}>
            <TrackVisibility>
              {({ isVisible }) =>
                <div className={isVisible ? "animate__animated animate__zoomIn" : ""}>
                  <img src={healty1} alt="Header Img"
                  />
                </div>}
            </TrackVisibility>
          </Col>
          <Col xs={12} md={8} xl={7}>
            <TrackVisibility>
              {({ isVisible }) =>
                <div className={isVisible ? "animate__animated animate__fadeIn" : ""}>
                  <h1 className="txt-rotate">HealthyLiving!</h1>
                  <p>Di HealthyLiving, kami berkomitmen untuk membantu Anda meningkatkan kualitas hidup melalui gaya hidup sehat. Kami percaya bahwa kesehatan adalah aset berharga yang harus dijaga dan diperhatikan dengan baik</p>
                  <button onClick={() => console.log('connect')}>Let’s Connect <ArrowRightCircle size={25} /></button>
                </div>}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>

      <Container id="food">
        <Row className="aligh-items-center">
          <Col xs={12} md={8} xl={7}>
            <TrackVisibility>
              {({ isVisible }) =>
                <div className={isVisible ? "animate__animated animate__fadeIn" : ""}>
                  <h2 className="txt-rotate">OLAHRAGA YANG TERATUR</h2>
